# core/urls.py - VERSIONE COMPLETA CON ROUTE PROFILO E AZIENDAfrom django.urls import pathfrom . import viewsapp_name = 'core'urlpatterns = [    # Landing e registrazione    path('', views.LandingPageView.as_view(), name='landing'),    path('register/', views.CompanyRegistrationView.as_view(), name='company_registration'),    path('welcome/', views.RegistrationSuccessView.as_view(), name='registration_success'),    path('onboarding/', views.CompanyOnboardingView.as_view(), name='company_onboarding'),        # Dashboard principale    path('dashboard/', views.DashboardView.as_view(), name='dashboard'),        # Analytics e Report    path('analytics/', views.AnalyticsView.as_view(), name='analytics'),    path('reports/', views.ReportsView.as_view(), name='reports'),        # Profilo Utente e Azienda - NUOVE ROUTE    path('profile/', views.UserProfileView.as_view(), name='user_profile'),    path('profile/edit/', views.UserProfileEditView.as_view(), name='user_profile_edit'),    path('company/edit/', views.CompanyEditView.as_view(), name='company_edit'),    path('settings/', views.CompanySettingsView.as_view(), name='company_settings'),        # Gestione ordini    path('orders/', views.OrderListView.as_view(), name='order_list'),    path('orders/create/', views.OrderCreateView.as_view(), name='order_create'),    path('orders/<uuid:pk>/', views.OrderDetailView.as_view(), name='order_detail'),    path('orders/<uuid:pk>/edit/', views.OrderUpdateView.as_view(), name='order_edit'),    path('orders/<uuid:pk>/assign/<int:technician_id>/', views.assign_order, name='assign_order'),        # Gestione clienti    path('customers/', views.CustomerListView.as_view(), name='customer_list'),    path('customers/create/', views.CustomerCreateView.as_view(), name='customer_create'),    path('customers/<int:pk>/', views.CustomerDetailView.as_view(), name='customer_detail'),    path('customers/<int:pk>/edit/', views.CustomerUpdateView.as_view(), name='customer_edit'),        # Gestione tecnici    path('technicians/', views.TechnicianListView.as_view(), name='technician_list'),    path('technicians/create/', views.TechnicianCreateView.as_view(), name='technician_create'),    path('technicians/<int:pk>/', views.TechnicianDetailView.as_view(), name='technician_detail'),    path('technicians/<int:pk>/edit/', views.TechnicianUpdateView.as_view(), name='technician_edit'),    path('technicians/<int:pk>/locate/', views.technician_locate, name='technician_locate'),        # Mappa in tempo reale    path('map/', views.LiveMapView.as_view(), name='live_map'),        # Autenticazione    path('login/', views.CustomLoginView.as_view(), name='login'),    path('logout/', views.CustomLogoutView.as_view(), name='logout'),]